import React from "react";

class SearchBar extends React.Component {
    state = { term: ""}

    render() {
        return (
            <div className="ui segment">
                <form className="ui form">
                    <input type="text" placeholder="Search images" value={this.state.term} onChange={e => this.setState({term : e.target.value.toUpperCase()})}/>
                </form>
            </div>
        );
    };
};

export default SearchBar;
